        ];

        const rows = books.map(book => {
          const row = [
            book.title || '',
            book.author || '',
            book.genre || '',
            book.status || '',
            book.priority?.toString() || '',
            book.format || '',
            book.totalPages?.toString() || '',
            book.currentPage?.toString() || '',
            `${Math.round((book.progress || 0) * 100)}%`,
