import { useState, useEffect } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Progress } from "@/components/ui/progress";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { ReadingSessionHistory } from "./reading-session-history";
import { BookOpen, Clock, Target, TrendingUp, Lightbulb } from "lucide-react";
import { format, differenceInDays } from "date-fns";

interface ReadingSession {
  id: string;
  startPage: number;
  endPage: number;
  sessionDate: string;
  notes?: string;
}

interface ReadingProgressProps {
  id: string;
  title: string;
  author: string;
  totalPages: number;
  currentPage: number;
  startedAt?: string;
  sessions?: ReadingSession[];
  onUpdateProgress?: (id: string, newPage: number) => void;
  onAddSession?: (id: string, session: { startPage: number; endPage: number; notes?: string }) => void;
  onEditSession?: (id: string, sessionId: string, updates: { startPage: number; endPage: number; notes?: string }) => void;
}

export function ReadingProgress({
  id,
  title,
  author,
  totalPages,
  currentPage,
  startedAt,
  sessions = [],
  onUpdateProgress,
  onAddSession,
  onEditSession,
}: ReadingProgressProps) {
  const [newPage, setNewPage] = useState(currentPage);
  const [isUpdating, setIsUpdating] = useState(false);

  // Sync input with current page when it changes via sessions
  useEffect(() => {
    setNewPage(currentPage);
  }, [currentPage]);

  const progress = (currentPage / totalPages) * 100;
  const remainingPages = totalPages - currentPage;
  const daysReading = startedAt ? differenceInDays(new Date(), new Date(startedAt)) + 1 : 1;
  const pagesPerDay = currentPage / daysReading;
  const estimatedDaysToFinish = pagesPerDay > 0 ? Math.ceil(remainingPages / pagesPerDay) : 0;

  const handleUpdateProgress = async () => {
    // Validate page bounds
    const validPage = Math.max(0, Math.min(newPage, totalPages));
    if (validPage !== newPage) {
      setNewPage(validPage);
    }

    if (validPage === currentPage) return;

    setIsUpdating(true);
    console.log(`Updating progress for ${title}: page ${validPage}`);

    // Simulate API call
    await new Promise(resolve => setTimeout(resolve, 500));

    onUpdateProgress?.(id, validPage);
    setIsUpdating(false);
  };

  const handleAddSession = (session: { startPage: number; endPage: number; notes?: string }) => {
    onAddSession?.(id, session);
  };

  const handleEditSession = (sessionId: string, updates: { startPage: number; endPage: number; notes?: string }) => {
    onEditSession?.(id, sessionId, updates);
  };

  return (
    <Card data-testid={`card-reading-progress-${id}`}>
      <CardHeader>
        <CardTitle className="flex items-center gap-2 text-lg">
          <BookOpen className="h-5 w-5" />
          <div className="flex-1">
            <div className="font-serif" data-testid={`text-progress-title-${id}`}>{title}</div>
            <div className="text-sm font-normal text-muted-foreground" data-testid={`text-progress-author-${id}`}>
              by {author}
            </div>
          </div>
        </CardTitle>
      </CardHeader>
      <CardContent>
        <Tabs defaultValue="progress" className="w-full">
          <TabsList className="grid w-full grid-cols-2">
            <TabsTrigger value="progress" data-testid={`tab-progress-${id}`}>
              Progress
            </TabsTrigger>
            <TabsTrigger value="history" data-testid={`tab-history-${id}`}>
              Sessions ({sessions.length})
            </TabsTrigger>
          </TabsList>

          <TabsContent value="progress" className="space-y-4 mt-4">
            <div className="space-y-2">
              <div className="flex justify-between items-center">
                <span className="text-sm font-medium">Reading Progress</span>
                <Badge variant="outline" data-testid={`badge-progress-percent-${id}`}>
                  {Math.round(progress)}% Complete
                </Badge>
              </div>
              <Progress value={progress} className="h-3" data-testid={`progress-bar-${id}`} />
              <div className="flex justify-between text-sm text-muted-foreground">
                <span data-testid={`text-current-page-${id}`}>Page {currentPage}</span>
                <span data-testid={`text-total-pages-${id}`}>{totalPages} pages</span>
              </div>
            </div>

            <div className="grid grid-cols-3 gap-4 text-center">
              <div className="space-y-1">
                <div className="flex items-center justify-center gap-1">
                  <Clock className="h-4 w-4 text-muted-foreground" />
                </div>
                <div className="text-lg font-semibold" data-testid={`text-days-reading-${id}`}>{daysReading}</div>
                <div className="text-xs text-muted-foreground">Days Reading</div>
              </div>
              <div className="space-y-1">
                <div className="flex items-center justify-center gap-1">
                  <TrendingUp className="h-4 w-4 text-muted-foreground" />
                </div>
                <div className="text-lg font-semibold" data-testid={`text-pages-per-day-${id}`}>
                  {pagesPerDay.toFixed(1)}
                </div>
                <div className="text-xs text-muted-foreground">Pages/Day</div>
              </div>
              <div className="space-y-1">
                <div className="flex items-center justify-center gap-1">
                  <Target className="h-4 w-4 text-muted-foreground" />
                </div>
                <div className="text-lg font-semibold" data-testid={`text-estimated-days-${id}`}>
                  {estimatedDaysToFinish}
                </div>
                <div className="text-xs text-muted-foreground">Days Left</div>
              </div>
            </div>

            {startedAt && (
              <div className="text-sm text-muted-foreground text-center" data-testid={`text-started-date-${id}`}>
                Started {format(new Date(startedAt), "MMM dd, yyyy")}
              </div>
            )}

            <div className="space-y-2">
              <div className="flex gap-2">
                <Input
                  type="number"
                  value={newPage}
                  onChange={(e) => setNewPage(parseInt(e.target.value) || 0)}
                  min={0}
                  max={totalPages}
                  placeholder="Update page"
                  className="flex-1"
                  data-testid={`input-update-page-${id}`}
                />
                <Button 
                  onClick={handleUpdateProgress}
                  disabled={isUpdating || newPage === currentPage}
                  data-testid={`button-update-progress-${id}`}
                >
                  {isUpdating ? "Updating..." : "Update"}
                </Button>
              </div>
              {newPage > currentPage && (
                <div className="flex items-center gap-2 text-xs text-muted-foreground bg-muted/50 p-2 rounded" data-testid={`sync-note-${id}`}>
                  <Lightbulb className="h-3 w-3 text-blue-500" />
                  <span>This will create a reading session: pages {currentPage}â†’{newPage}</span>
                </div>
              )}
            </div>
          </TabsContent>

          <TabsContent value="history" className="mt-4">
            <ReadingSessionHistory 
              bookId={id}
              currentPage={currentPage}
              sessions={sessions}
              onAddSession={handleAddSession}
              onEditSession={handleEditSession}
            />
          </TabsContent>
        </Tabs>
      </CardContent>
    </Card>
  );
}

client/src/components/reading-session-hi.content:
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { History, Calendar, BookOpen, Plus, Edit2, Save, X } from "lucide-react";
import { format, formatDistanceToNow } from "date-fns";
import { useState } from "react";

interface ReadingSession {
  id: string;
  startPage: number;
  endPage: number;
  sessionDate: string;
  notes?: string;
}

interface ReadingSessionHistoryProps {
  bookId: string;
  currentPage: number;
  sessions: ReadingSession[];
  onAddSession?: (session: { startPage: number; endPage: number; notes?: string }) => void;
  onEditSession?: (sessionId: string, updates: { startPage: number; endPage: number; notes?: string }) => void;
}

export function ReadingSessionHistory({ bookId, currentPage, sessions, onAddSession, onEditSession }: ReadingSessionHistoryProps) {
  const [isAddingSession, setIsAddingSession] = useState(false);
  const [editingSessionId, setEditingSessionId] = useState<string | null>(null);
  const [newSession, setNewSession] = useState({
    startPage: "",
    endPage: "", 
    notes: "",
  });
  const [editSession, setEditSession] = useState({
    startPage: "",
    endPage: "",
    notes: "",
  });

  const handleStartAddingSession = () => {
    setNewSession({
      startPage: currentPage.toString(),
      endPage: "",
      notes: "",
    });
    setIsAddingSession(true);
  };

  const totalPagesRead = sessions.reduce((sum, session) => 
    sum + (session.endPage - session.startPage), 0
  );

  const averageSessionLength = sessions.length > 0 
    ? Math.round(totalPagesRead / sessions.length) 
    : 0;

  const handleAddSession = () => {
    const startPage = parseInt(newSession.startPage);
    const endPage = parseInt(newSession.endPage);

    if (startPage >= 0 && endPage > startPage) {
      onAddSession?.({
        startPage,
        endPage,
        notes: newSession.notes.trim() || undefined,
      });
      setNewSession({ startPage: "", endPage: "", notes: "" });
      setIsAddingSession(false);
      console.log(`Added reading session: pages ${startPage}-${endPage}`);
    }
  };

  const handleStartEdit = (session: ReadingSession) => {
    setEditSession({
      startPage: session.startPage.toString(),
      endPage: session.endPage.toString(),
      notes: session.notes || "",
    });
    setEditingSessionId(session.id);
    console.log(`Started editing session ${session.id}`);
  };

  const handleSaveEdit = () => {
    if (!editingSessionId) return;

    const startPage = parseInt(editSession.startPage);
    const endPage = parseInt(editSession.endPage);

    if (startPage >= 0 && endPage > startPage) {
      onEditSession?.(editingSessionId, {
        startPage,
        endPage,
        notes: editSession.notes.trim() || undefined,
      });
      setEditingSessionId(null);
      setEditSession({ startPage: "", endPage: "", notes: "" });
      console.log(`Saved changes to session ${editingSessionId}: pages ${startPage}-${endPage}`);
    }
  };

  const handleCancelEdit = () => {
    setEditingSessionId(null);
    setEditSession({ startPage: "", endPage: "", notes: "" });
    console.log("Cancelled session edit");
  };

  const sortedSessions = [...sessions].sort((a, b) => 
    new Date(b.sessionDate).getTime() - new Date(a.sessionDate).getTime()
  );

  return (
    <Card data-testid={`card-session-history-${bookId}`}>
