import { useState } from 'react';
import { useQuery, useQueryClient } from '@tanstack/react-query';
import { Play, Pause, Square, Clock, BookOpen, Target, Calendar } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Progress } from '@/components/ui/progress';
import { ProgressInput } from '@/components/progress-input';
import { useSessionTimer } from '@/hooks/useSessionTimer';
import { useSessionState } from '@/hooks/useSessionState';
import { getProgressPercentage, calculateProgressForecast } from '@/lib/progressUtils';
import { formatSessionTime, getSessionDuration } from '@/lib/sessionUtils';
import type { Book, ReadingSession } from '@shared/schema';

interface HeroSessionCardProps {
  book: Book;
  activeSession?: ReadingSession | null;
  onSessionUpdate?: () => void;
}

export function HeroSessionCard({ book, activeSession, onSessionUpdate }: HeroSessionCardProps) {
  const queryClient = useQueryClient();
  const { actions, isLoading, error } = useSessionState(book.id);
  const { timer, startTimer, pauseTimer, resumeTimer, stopTimer, getFormattedTime } = useSessionTimer(activeSession);
  const [isStarting, setIsStarting] = useState(false);
  const [showProgressInput, setShowProgressInput] = useState(false);

  // Fetch recent sessions for progress forecasting
  const { data: recentSessions = [] } = useQuery({
    queryKey: ['/api/books', book.id, 'sessions'],
    queryFn: async () => {
      const response = await fetch(`/api/books/${book.id}/sessions?limit=10`);
      if (!response.ok) throw new Error('Failed to fetch sessions');
      const data = await response.json();
      return data.map((session: any) => ({
        ...session,
        startedAt: new Date(session.startedAt),
        sessionDate: new Date(session.sessionDate),
        pausedAt: session.pausedAt ? new Date(session.pausedAt) : null,
        resumedAt: session.resumedAt ? new Date(session.resumedAt) : null,
        endedAt: session.endedAt ? new Date(session.endedAt) : null,
      }));
    },
  });

  const progressPercentage = getProgressPercentage(book);
  const forecast = calculateProgressForecast(book, recentSessions);

  const handleStartSession = async () => {
    if (activeSession) return;

    setIsStarting(true);
    try {
      await actions.startSession({
        bookId: book.id,
        startPage: book.currentPage || 0,
      });
      startTimer();
      onSessionUpdate?.();
    } catch (err) {
      console.error('Failed to start session:', err);
    } finally {
      setIsStarting(false);
    }
  };

  const handlePauseSession = async () => {
    if (!activeSession) return;

    try {
      await actions.pauseSession(activeSession.id);
      pauseTimer();
      onSessionUpdate?.();
    } catch (err) {
      console.error('Failed to pause session:', err);
    }
  };

  const handleResumeSession = async () => {
    if (!activeSession || activeSession.state !== 'paused') return;

    try {
      await actions.resumeSession(activeSession.id);
      resumeTimer();
      onSessionUpdate?.();
    } catch (err) {
      console.error('Failed to resume session:', err);
    }
  };

  const handleStopSession = () => {
    if (!activeSession) return;
    setShowProgressInput(true);
  };

  const handleProgressUpdate = async (endPage: number) => {
    if (!activeSession) return;

    try {
      // Update book progress first
      const response = await fetch(`/api/books/${book.id}/progress`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ currentPage: endPage }),
      });

      if (!response.ok) throw new Error('Failed to update progress');

      // Stop the session with the updated end page
      await actions.stopSession({
        sessionId: activeSession.id,
        endPage,
      });

      stopTimer();
      setShowProgressInput(false);

      // Invalidate relevant queries
      queryClient.invalidateQueries({ queryKey: ['/api/books/currently-reading'] });
      queryClient.invalidateQueries({ queryKey: ['/api/books', book.id] });
      queryClient.invalidateQueries({ queryKey: ['/api/books', book.id, 'sessions'] });

      onSessionUpdate?.();
    } catch (err) {
      console.error('Failed to stop session:', err);
    }
  };

  const handleCancelProgressInput = () => {
    setShowProgressInput(false);
  };

  const getSessionStatusInfo = () => {
    if (!activeSession) {
      return {
        status: 'Ready to start',
        color: 'gray' as const,
        action: 'start',
      };
    }

    switch (activeSession.state) {
      case 'active':
        return {
          status: 'Reading session active',
          color: 'green' as const,
          action: 'pause',
        };
      case 'paused':
        return {
          status: 'Session paused',
          color: 'orange' as const,
          action: 'resume',
        };
      default:
        return {
          status: 'Ready to start',
          color: 'gray' as const,
          action: 'start',
        };
    }
  };

  const statusInfo = getSessionStatusInfo();

  return (
    <Card className="w-full" data-testid="card-hero-session">
      <CardHeader>
        <div className="flex items-start justify-between">
          <div className="space-y-1">
            <CardTitle className="text-xl font-serif">{book.title}</CardTitle>
            <p className="text-muted-foreground">by {book.author}</p>
          </div>
          <Badge 
            variant={statusInfo.color === 'green' ? 'default' : statusInfo.color === 'orange' ? 'secondary' : 'outline'}
            data-testid="badge-session-status"
          >
            {statusInfo.status}
          </Badge>
        </div>
      </CardHeader>

      <CardContent className="space-y-6">
        {/* Progress Section */}
        <div className="space-y-3">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-2">
              <BookOpen className="h-4 w-4 text-muted-foreground" />
              <span className="text-sm font-medium">Reading Progress</span>
            </div>
            <span className="text-sm text-muted-foreground">
              {book.currentPage || 0} of {book.totalPages || 0} pages
            </span>
          </div>
          <Progress value={progressPercentage} className="h-2" data-testid="progress-book" />
          <div className="text-xs text-muted-foreground text-right">
            {progressPercentage}% complete
          </div>
        </div>

        {/* Session Timer */}
        <div className="space-y-3">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-2">
              <Clock className="h-4 w-4 text-muted-foreground" />
              <span className="text-sm font-medium">Session Time</span>
            </div>
            {activeSession && (
              <span className="text-xs text-muted-foreground">
                Started {formatSessionTime(activeSession.startedAt)}
              </span>
            )}
          </div>
          <div className="text-center">
            <div className="text-3xl font-mono font-bold" data-testid="text-timer">
              {getFormattedTime()}
            </div>
            {activeSession && activeSession.duration && (
              <div className="text-xs text-muted-foreground mt-1">
                Total: {getSessionDuration(activeSession)}
              </div>
            )}
          </div>
        </div>

        {/* Session Controls */}
        <div className="flex justify-center space-x-3">
          {!activeSession && (
            <Button
              size="lg"
              onClick={handleStartSession}
              disabled={isStarting || isLoading}
              data-testid="button-start-session"
            >
              <Play className="h-4 w-4 mr-2" />
              {isStarting ? 'Starting...' : 'Start Reading'}
            </Button>
          )}

          {activeSession?.state === 'active' && (
            <>
              <Button
                size="lg"
                variant="outline"
                onClick={handlePauseSession}
                disabled={isLoading}
                data-testid="button-pause-session"
              >
                <Pause className="h-4 w-4 mr-2" />
                Pause
              </Button>
              <Button
                size="lg"
                variant="destructive"
                onClick={handleStopSession}
                disabled={isLoading}
                data-testid="button-stop-session"
              >
                <Square className="h-4 w-4 mr-2" />
                Stop
              </Button>
            </>
          )}

          {activeSession?.state === 'paused' && (
            <>
              <Button
                size="lg"
                onClick={handleResumeSession}
                disabled={isLoading}
                data-testid="button-resume-session"
              >
                <Play className="h-4 w-4 mr-2" />
                Resume
              </Button>
              <Button
                size="lg"
                variant="destructive"
                onClick={handleStopSession}
                disabled={isLoading}
                data-testid="button-stop-session"
              >
                <Square className="h-4 w-4 mr-2" />
                Stop
              </Button>
            </>
          )}
        </div>

        {/* Progress Forecast */}
        {forecast.estimatedTimeToFinish && (
          <div className="space-y-3 pt-4 border-t">
            <div className="flex items-center space-x-2">
              <Target className="h-4 w-4 text-muted-foreground" />
              <span className="text-sm font-medium">Reading Forecast</span>
            </div>
            <div className="grid grid-cols-2 gap-4 text-sm">
              <div>
                <div className="text-muted-foreground">Time to finish</div>
                <div className="font-medium" data-testid="text-time-to-finish">
                  {forecast.estimatedTimeToFinish}
                </div>
              </div>
              <div>
                <div className="text-muted-foreground">Reading pace</div>
                <div className="font-medium" data-testid="text-reading-pace">
                  {forecast.averagePagesPerHour > 0 ? 
                    `${forecast.averagePagesPerHour.toFixed(1)} pages/hr` : 
                    'No data yet'
                  }
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Progress Input */}
        {showProgressInput && (
          <div className="pt-4 border-t">
            <ProgressInput
              book={book}
              onPageUpdate={() => {}} // Not used in this context
              onCancel={handleCancelProgressInput}
              onConfirm={handleProgressUpdate}
              isLoading={isLoading}
            />
          </div>
        )}

        {/* Error Display */}
        {error && (
          <div className="text-sm text-destructive bg-destructive/10 p-3 rounded-md" data-testid="text-session-error">
            {error}
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/notes/edit-note-sh.content:
import { useState, useEffect } from 'react';
import { useMutation, useQueryClient, useQuery } from '@tanstack/react-query';
import { 
  PenTool, 
  Quote, 
  Highlighter, 
  FileText, 
  CheckSquare,
  Calendar,
  Link as LinkIcon,
  Copy,
  ArrowRightLeft,
  Brain
} from 'lucide-react';
import {
  Sheet,
  SheetContent,
  SheetDescription,
  SheetFooter,
  SheetHeader,
  SheetTitle,
} from '@/components/ui/sheet';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Badge } from '@/components/ui/badge';
import { Switch } from '@/components/ui/switch';
import { Label } from '@/components/ui/label';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from '@/components/ui/dropdown-menu';
import { apiRequest } from '@/lib/queryClient';
import { formatDistanceToNow } from 'date-fns';
import type { BookNote, Book, UpdateBookNote } from '@shared/schema';

interface EditNoteSheetProps {
  note: BookNote | null;
  isOpen: boolean;
  onOpenChange: (open: boolean) => void;
  onUpdated?: (note: BookNote) => void;
  onDeleted?: (noteId: string) => void;
}

const NOTE_TYPES = [
  { id: 'note', label: 'Note', icon: PenTool, color: 'bg-blue-100 text-blue-700' },
  { id: 'quote', label: 'Quote', icon: Quote, color: 'bg-green-100 text-green-700' },
  { id: 'highlight', label: 'Highlight', icon: Highlighter, color: 'bg-yellow-100 text-yellow-700' },
  { id: 'summary', label: 'Summary', icon: FileText, color: 'bg-purple-100 text-purple-700' },
  { id: 'action', label: 'Action', icon: CheckSquare, color: 'bg-orange-100 text-orange-700' },
];

const CONVERSION_OPTIONS = [
  { from: 'quote', to: 'summary', label: 'Quote â†’ Summary' },
  { from: 'note', to: 'action', label: 'Note â†’ Action' },
  { from: 'highlight', to: 'note', label: 'Highlight â†’ Note' },
  { from: 'summary', to: 'note', label: 'Summary â†’ Note' },
  { from: 'action', to: 'note', label: 'Action â†’ Note' },
];

export function EditNoteSheet({ 
  note, 
  isOpen, 
  onOpenChange, 
  onUpdated, 
  onDeleted 
}: EditNoteSheetProps) {
  const queryClient = useQueryClient();
  const [content, setContent] = useState('');
  const [selectedBookId, setSelectedBookId] = useState('');
  const [page, setPage] = useState('');
  const [noteType, setNoteType] = useState('note');
  const [makeRecall, setMakeRecall] = useState(false);
  const [newTag, setNewTag] = useState('');
  const [tags, setTags] = useState<string[]>([]);

  // Fetch all books for book selector
  const { data: allBooks = [] } = useQuery({
    queryKey: ['/api/books'],
    queryFn: async () => {
      const response = await fetch('/api/books');
      if (!response.ok) throw new Error('Failed to fetch books');
      const data = await response.json();
      return data.map((book: any) => ({
        ...book,
        addedAt: new Date(book.addedAt),
        lastReadAt: book.lastReadAt ? new Date(book.lastReadAt) : null,
        startedAt: book.startedAt ? new Date(book.startedAt) : null,
        completedAt: book.completedAt ? new Date(book.completedAt) : null,
      })) as Book[];
    },
    enabled: isOpen,
  });

  // Reset form when note changes
  useEffect(() => {
    if (note) {
      setContent(note.content || '');
      setSelectedBookId(note.bookId);
      setPage(note.page?.toString() || '');
      setNoteType(note.noteType);
      setTags(note.tags || []);
      setMakeRecall(false);
      setNewTag('');
    }
  }, [note]);

  // Update note mutation
  const updateNoteMutation = useMutation({
    mutationFn: async (noteData: UpdateBookNote) => {
      if (!note) throw new Error('No note to update');
      const response = await fetch(`/api/notes/${note.id}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(noteData),
      });
      if (!response.ok) throw new Error('Failed to update note');
      return await response.json();
    },
    onSuccess: (updatedNote: BookNote) => {
      queryClient.invalidateQueries({ queryKey: ['/api/notes'] });
      onUpdated?.(updatedNote);
      onOpenChange(false);
    },
    onError: (error) => {
      console.error('Failed to update note:', error);
    },
  });

  // Delete note mutation
  const deleteNoteMutation = useMutation({
    mutationFn: async () => {
      if (!note) throw new Error('No note to delete');
      const response = await fetch(`/api/notes/${note.id}`, {
        method: 'DELETE',
      });
      if (!response.ok) throw new Error('Failed to delete note');
      return response;
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/notes'] });
      onDeleted?.(note!.id);
      onOpenChange(false);
    },
    onError: (error) => {
      console.error('Failed to delete note:', error);
    },
  });

  const handleSave = () => {
    if (!content.trim() || !selectedBookId) return;

    const noteData: UpdateBookNote = {
      content: content.trim(),
      bookId: selectedBookId,
      page: page ? parseInt(page) : undefined,
      noteType: noteType as any,
      tags: tags,
    };

    updateNoteMutation.mutate(noteData);
  };

  const handleDelete = () => {
    if (window.confirm('Are you sure you want to delete this note?')) {
      deleteNoteMutation.mutate();
    }
  };

  const handleAddTag = () => {
    if (newTag.trim() && !tags.includes(newTag.trim())) {
      setTags([...tags, newTag.trim()]);
      setNewTag('');
    }
  };

  const removeTag = (tagToRemove: string) => {
    setTags(tags.filter(tag => tag !== tagToRemove));
  };

  const handleConvertTo = (newType: string) => {
    setNoteType(newType);
  };

  const copyNoteLink = () => {
    if (note) {
      const link = `app://note/${note.id}`;
      navigator.clipboard.writeText(link);
      // Could show a toast here
    }
  };

  const selectedType = NOTE_TYPES.find(type => type.id === noteType) || NOTE_TYPES[0];
  const availableConversions = CONVERSION_OPTIONS.filter(option => option.from === noteType);
  const currentBook = allBooks.find(book => book.id === selectedBookId);

  if (!note) return null;

  return (
    <Sheet open={isOpen} onOpenChange={onOpenChange}>
      <SheetContent className="w-full sm:max-w-lg overflow-y-auto" data-testid="sheet-edit-note">
        <SheetHeader>
          <SheetTitle>Edit Note</SheetTitle>
          <SheetDescription>
            Make changes to your note. All fields are optional except content.
          </SheetDescription>
        </SheetHeader>

        <div className="py-6 space-y-6">
          {/* Note content */}
          <div className="space-y-2">
            <Label htmlFor="content">Content</Label>
            <Textarea
              id="content"
              placeholder="Write your note content..."
              value={content}
              onChange={(e) => setContent(e.target.value)}
              className="min-h-[120px] resize-none"
              data-testid="textarea-edit-content"
            />
            <div className="text-xs text-muted-foreground">
              {content.length} characters
            </div>
          </div>

          {/* Book and page */}
          <div className="grid grid-cols-3 gap-3">
            <div className="col-span-2 space-y-2">
              <Label>Book</Label>
              <Select value={selectedBookId} onValueChange={setSelectedBookId}>
                <SelectTrigger data-testid="select-edit-book">
                  <SelectValue placeholder="Select book" />
                </SelectTrigger>
                <SelectContent>
                  {allBooks.map(book => (
                    <SelectItem key={book.id} value={book.id}>
                      {book.title} - {book.author}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            <div className="space-y-2">
              <Label>Page</Label>
              <Input
                type="number"
                placeholder="Page"
                value={page}
                onChange={(e) => setPage(e.target.value)}
                data-testid="input-edit-page"
              />
            </div>
          </div>

          {/* Note type */}
          <div className="space-y-3">
            <Label>Type</Label>
            <div className="flex gap-2 flex-wrap">
              {NOTE_TYPES.map(type => {
                const Icon = type.icon;
                const isSelected = noteType === type.id;
                return (
                  <Button
                    key={type.id}
                    variant={isSelected ? "default" : "outline"}
                    size="sm"
                    onClick={() => setNoteType(type.id)}
                    className={`${isSelected ? type.color : ''} gap-1`}
                    data-testid={`button-edit-type-${type.id}`}
                  >
                    <Icon className="h-3 w-3" />
                    {type.label}
                  </Button>
                );
              })}
            </div>
          </div>

          {/* Conversion shortcuts */}
          {availableConversions.length > 0 && (
            <div className="space-y-2">
              <Label>Convert to...</Label>
              <div className="flex gap-2 flex-wrap">
                {availableConversions.map(conversion => (
                  <Button
                    key={conversion.to}
                    variant="outline"
                    size="sm"
                    onClick={() => handleConvertTo(conversion.to)}
                    className="gap-1"
                    data-testid={`button-convert-${conversion.to}`}
                  >
                    <ArrowRightLeft className="h-3 w-3" />
                    {conversion.label}
                  </Button>
                ))}
              </div>
            </div>
          )}

          {/* Tags */}
          <div className="space-y-3">
            <Label>Tags</Label>
            <div className="flex gap-2">
              <Input
                placeholder="Add tag"
                value={newTag}
                onChange={(e) => setNewTag(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && handleAddTag()}
                data-testid="input-edit-tag"
              />
              <Button onClick={handleAddTag} size="sm" data-testid="button-edit-add-tag">
                Add
              </Button>
            </div>
            {tags.length > 0 && (
              <div className="flex gap-1 flex-wrap">
                {tags.map(tag => (
                  <Badge
                    key={tag}
                    variant="secondary"
                    className="cursor-pointer"
                    onClick={() => removeTag(tag)}
                    data-testid={`edit-tag-${tag}`}
                  >
                    {tag} Ã—
                  </Badge>
                ))}
              </div>
            )}
          </div>

          {/* Recall toggle */}
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-2">
              <Switch
                id="make-recall-edit"
                checked={makeRecall}
                onCheckedChange={setMakeRecall}
                data-testid="switch-edit-recall"
              />
              <Label htmlFor="make-recall-edit" className="flex items-center gap-2">
                <Brain className="h-4 w-4" />
                Make Recall
              </Label>
            </div>
          </div>

          {/* Metadata */}
          <div className="space-y-2 pt-4 border-t">
            <div className="flex items-center justify-between text-sm text-muted-foreground">
              <div className="flex items-center gap-2">
                <Calendar className="h-4 w-4" />
                <span>Created {formatDistanceToNow(new Date(note.createdAt), { addSuffix: true })}</span>
              </div>
              <Button
                variant="ghost"
                size="sm"
                onClick={copyNoteLink}
                data-testid="button-copy-link"
              >
                <LinkIcon className="h-4 w-4 mr-1" />
                Copy Link
              </Button>
            </div>
          </div>
        </div>

        <SheetFooter className="gap-2">
          <Button
            variant="destructive"
            onClick={handleDelete}
            disabled={deleteNoteMutation.isPending}
            data-testid="button-delete-note"
          >
            {deleteNoteMutation.isPending ? 'Deleting...' : 'Delete'}
          </Button>
          <Button
            variant="outline"
            onClick={() => onOpenChange(false)}
            data-testid="button-cancel-edit"
          >
            Cancel
          </Button>
          <Button
            onClick={handleSave}
            disabled={!content.trim() || !selectedBookId || updateNoteMutation.isPending}
            data-testid="button-save-edit"
          >
            {updateNoteMutation.isPending ? 'Saving...' : 'Save Changes'}
          </Button>
        </SheetFooter>
      </SheetContent>
    </Sheet>
  );
}

client/src/components/notes/header-searc.content:
import { useState } from 'react';
import { Search, X, Filter, Calendar } from 'lucide-react';
import { Input } from '@/components/ui/input';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { ScrollArea } from '@/components/ui/scroll-area';
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
  DropdownMenuCheckboxItem,
} from '@/components/ui/dropdown-menu';
import type { Book } from '@shared/schema';

interface FilterState {
  books: string[];
  types: string[];
  tags: string[];
  hasImage: boolean | null;
  dateRange: string | null;
}

interface HeaderSearchFiltersProps {
  searchTerm: string;
  onSearchChange: (search: string) => void;
  filters: FilterState;
  onFiltersChange: (filters: FilterState) => void;
  onQuickCaptureOpen: () => void;
  availableBooks: Book[];
  availableTags: string[];
  noteCount: number;
}

const NOTE_TYPES = [
  { id: 'note', label: 'Note' },
  { id: 'quote', label: 'Quote' },
  { id: 'highlight', label: 'Highlight' },
  { id: 'summary', label: 'Summary' },
  { id: 'action', label: 'Action' },
];

const DATE_RANGES = [
  { id: 'today', label: 'Today' },
  { id: '7d', label: '7 days' },
  { id: '30d', label: '30 days' },
  { id: 'custom', label: 'Custom' },
];

export function HeaderSearchFilters({
  searchTerm,
  onSearchChange,
  filters,
  onFiltersChange,
  onQuickCaptureOpen,
  availableBooks,
  availableTags,
  noteCount,
}: HeaderSearchFiltersProps) {
  const [isFilterOpen, setIsFilterOpen] = useState(false);

  const activeFilterCount = 
    filters.books.length + 
    filters.types.length + 
    filters.tags.length + 
    (filters.hasImage !== null ? 1 : 0) + 
    (filters.dateRange ? 1 : 0);

  const clearAllFilters = () => {
    onFiltersChange({
      books: [],
      types: [],
      tags: [],
      hasImage: null,
      dateRange: null,
    });
  };

  const toggleBookFilter = (bookId: string) => {
    const newBooks = filters.books.includes(bookId)
      ? filters.books.filter(id => id !== bookId)
      : [...filters.books, bookId];
    onFiltersChange({ ...filters, books: newBooks });
  };

  const toggleTypeFilter = (typeId: string) => {
    const newTypes = filters.types.includes(typeId)
      ? filters.types.filter(id => id !== typeId)
      : [...filters.types, typeId];
    onFiltersChange({ ...filters, types: newTypes });
  };

  const toggleTagFilter = (tag: string) => {
    const newTags = filters.tags.includes(tag)
      ? filters.tags.filter(t => t !== tag)
      : [...filters.tags, tag];
    onFiltersChange({ ...filters, tags: newTags });
  };

  const removeFilter = (type: string, value: string) => {
    switch (type) {
      case 'book':
        toggleBookFilter(value);
        break;
      case 'type':
        toggleTypeFilter(value);
        break;
      case 'tag':
        toggleTagFilter(value);
        break;
      case 'hasImage':
        onFiltersChange({ ...filters, hasImage: null });
        break;
      case 'dateRange':
        onFiltersChange({ ...filters, dateRange: null });
        break;
    }
  };

  const getBookTitle = (bookId: string) => {
    const book = availableBooks.find(b => b.id === bookId);
    return book?.title || 'Unknown Book';
  };

  const recentBooks = availableBooks.slice(0, 5);
  const frequentTags = availableTags.slice(0, 10);

  return (
    <div className="sticky top-0 z-10 bg-background border-b">
      <div className="p-4 space-y-4">
        {/* Header with search and add button */}
        <div className="flex items-center gap-3">
          <div className="relative flex-1">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground h-4 w-4" />
            <Input
              placeholder="Search notes, quotes, tags"
              value={searchTerm}
              onChange={(e) => onSearchChange(e.target.value)}
              className="pl-10 pr-10"
              data-testid="input-search-notes"
            />
            {searchTerm && (
              <Button
                variant="ghost"
                size="sm"
                className="absolute right-1 top-1/2 transform -translate-y-1/2 h-8 w-8 p-0"
                onClick={() => onSearchChange('')}
              >
                <X className="h-4 w-4" />
              </Button>
            )}
          </div>
          <Button onClick={onQuickCaptureOpen} data-testid="button-quick-capture">
            +
          </Button>
        </div>

        {/* Filter chips and controls */}
        <div className="flex items-center gap-2">
          <DropdownMenu open={isFilterOpen} onOpenChange={setIsFilterOpen}>
            <DropdownMenuTrigger asChild>
              <Button variant="outline" size="sm" data-testid="button-filters">
                <Filter className="h-4 w-4 mr-2" />
                Filters
                {activeFilterCount > 0 && (
                  <Badge variant="secondary" className="ml-2 h-5 w-5 p-0 text-xs rounded-full">
                    {activeFilterCount}
                  </Badge>
                )}
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent className="w-80" align="start">
              {/* Books Filter */}
              <DropdownMenuLabel>Books (Recent 5)</DropdownMenuLabel>
              {recentBooks.map(book => (
                <DropdownMenuCheckboxItem
                  key={book.id}
                  checked={filters.books.includes(book.id)}
                  onCheckedChange={() => toggleBookFilter(book.id)}
                  data-testid={`filter-book-${book.id}`}
                >
                  {book.title}
                </DropdownMenuCheckboxItem>
              ))}
              {availableBooks.length > 5 && (
                <DropdownMenuItem className="text-xs text-muted-foreground">
                  All... (showing recent 5)
                </DropdownMenuItem>
              )}

              <DropdownMenuSeparator />

              {/* Note Types Filter */}
              <DropdownMenuLabel>Type</DropdownMenuLabel>
              {NOTE_TYPES.map(type => (
                <DropdownMenuCheckboxItem
                  key={type.id}
                  checked={filters.types.includes(type.id)}
                  onCheckedChange={() => toggleTypeFilter(type.id)}
                  data-testid={`filter-type-${type.id}`}
                >
                  {type.label}
                </DropdownMenuCheckboxItem>
              ))}

              <DropdownMenuSeparator />

              {/* Tags Filter */}
              <DropdownMenuLabel>Tags (Top 10)</DropdownMenuLabel>
              {frequentTags.map(tag => (
                <DropdownMenuCheckboxItem
                  key={tag}
                  checked={filters.tags.includes(tag)}
                  onCheckedChange={() => toggleTagFilter(tag)}
                  data-testid={`filter-tag-${tag}`}
                >
                  {tag}
                </DropdownMenuCheckboxItem>
              ))}
              {availableTags.length > 10 && (
                <DropdownMenuItem className="text-xs text-muted-foreground">
                  All... (showing top 10)
                </DropdownMenuItem>
              )}

              <DropdownMenuSeparator />

              {/* Has Image Filter */}
              <DropdownMenuCheckboxItem
                checked={filters.hasImage === true}
                onCheckedChange={(checked) => 
                  onFiltersChange({ ...filters, hasImage: checked ? true : null })
                }
                data-testid="filter-has-image"
              >
                Has image
              </DropdownMenuCheckboxItem>

              <DropdownMenuSeparator />

              {/* Date Range Filter */}
              <DropdownMenuLabel>Date</DropdownMenuLabel>
              {DATE_RANGES.map(range => (
                <DropdownMenuCheckboxItem
                  key={range.id}
                  checked={filters.dateRange === range.id}
                  onCheckedChange={(checked) => 
                    onFiltersChange({ ...filters, dateRange: checked ? range.id : null })
                  }
                  data-testid={`filter-date-${range.id}`}
                >
                  {range.label}
                </DropdownMenuCheckboxItem>
              ))}
            </DropdownMenuContent>
          </DropdownMenu>

          {activeFilterCount > 0 && (
            <Button
              variant="ghost"
              size="sm"
              onClick={clearAllFilters}
              data-testid="button-clear-filters"
            >
              Clear all
            </Button>
          )}

          <div className="text-sm text-muted-foreground">
            {noteCount} note{noteCount === 1 ? '' : 's'}
          </div>
        </div>

        {/* Active filter chips */}
        {activeFilterCount > 0 && (
          <ScrollArea className="w-full">
            <div className="flex gap-2 pb-2">
              {filters.books.map(bookId => (
                <Badge
                  key={bookId}
                  variant="secondary"
                  className="flex items-center gap-1"
                  data-testid={`chip-book-${bookId}`}
                >
                  {getBookTitle(bookId)}
                  <X
                    className="h-3 w-3 cursor-pointer"
                    onClick={() => removeFilter('book', bookId)}
                  />
                </Badge>
              ))}
              {filters.types.map(type => (
                <Badge
                  key={type}
                  variant="secondary"
                  className="flex items-center gap-1"
                  data-testid={`chip-type-${type}`}
                >
                  {NOTE_TYPES.find(t => t.id === type)?.label || type}
                  <X
                    className="h-3 w-3 cursor-pointer"
                    onClick={() => removeFilter('type', type)}
                  />
                </Badge>
              ))}
              {filters.tags.map(tag => (
                <Badge
                  key={tag}
                  variant="secondary"
                  className="flex items-center gap-1"
                  data-testid={`chip-tag-${tag}`}
                >
                  {tag}
                  <X
                    className="h-3 w-3 cursor-pointer"
                    onClick={() => removeFilter('tag', tag)}
                  />
                </Badge>
              ))}
              {filters.hasImage && (
                <Badge
                  variant="secondary"
                  className="flex items-center gap-1"
                  data-testid="chip-has-image"
                >
                  Has image
                  <X
                    className="h-3 w-3 cursor-pointer"
                    onClick={() => removeFilter('hasImage', '')}
                  />
                </Badge>
              )}
              {filters.dateRange && (
                <Badge
                  variant="secondary"
                  className="flex items-center gap-1"
                  data-testid={`chip-date-${filters.dateRange}`}
                >
                  {DATE_RANGES.find(d => d.id === filters.dateRange)?.label || filters.dateRange}
                  <X
                    className="h-3 w-3 cursor-pointer"
                    onClick={() => removeFilter('dateRange', '')}
                  />
                </Badge>
              )}
            </div>
          </ScrollArea>
        )}
      </div>
    </div>
  );
}

