import { useMemo } from 'react';
import { useVirtualizer } from '@tanstack/react-virtual';
import { useRef } from 'react';
import { formatDistanceToNow } from 'date-fns';
import { 
  PenTool, 
  Quote, 
  Highlighter, 
  FileText, 
  CheckSquare,
  Edit,
  Brain,
  MoreHorizontal,
  Calendar,
  BookOpen
} from 'lucide-react';
import { Card, CardContent } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from '@/components/ui/dropdown-menu';
import type { BookNote, Book } from '@shared/schema';

interface NotesListProps {
  notes: (BookNote & { book?: Book })[];
  onEditNote: (note: BookNote) => void;
  onDeleteNote: (noteId: string) => void;
  onMakeRecall: (noteId: string) => void;
}

const NOTE_TYPE_CONFIG = {
  note: { icon: PenTool, color: 'bg-blue-100 text-blue-700 border-blue-200', label: 'Note' },
  quote: { icon: Quote, color: 'bg-green-100 text-green-700 border-green-200', label: 'Quote' },
  highlight: { icon: Highlighter, color: 'bg-yellow-100 text-yellow-700 border-yellow-200', label: 'Highlight' },
  summary: { icon: FileText, color: 'bg-purple-100 text-purple-700 border-purple-200', label: 'Summary' },
  action: { icon: CheckSquare, color: 'bg-orange-100 text-orange-700 border-orange-200', label: 'Action' },
};

export function NotesListVirtualized({
  notes,
  onEditNote,
  onDeleteNote,
  onMakeRecall,
}: NotesListProps) {
  const parentRef = useRef<HTMLDivElement>(null);

  const virtualizer = useVirtualizer({
    count: notes.length,
    getScrollElement: () => parentRef.current,
    estimateSize: () => 150, // Estimated height per note card
    overscan: 5,
  });

  const virtualItems = virtualizer.getVirtualItems();

  const renderNote = (note: BookNote & { book?: Book }) => {
    const typeConfig = NOTE_TYPE_CONFIG[note.noteType as keyof typeof NOTE_TYPE_CONFIG] || NOTE_TYPE_CONFIG.note;
    const TypeIcon = typeConfig.icon;

    return (
      <Card 
        className="hover-elevate transition-all duration-200 group"
        data-testid={`note-card-${note.id}`}
      >
        <CardContent className="p-4">
          {/* Header with type pill, book info, and actions */}
          <div className="flex items-start justify-between mb-3">
            <div className="flex items-center gap-3 flex-1 min-w-0">
              <Badge 
                variant="secondary" 
                className={`${typeConfig.color} flex items-center gap-1 shrink-0`}
                data-testid={`note-type-${note.id}`}
              >
                <TypeIcon className="h-3 w-3" />
                {typeConfig.label}
              </Badge>

              <div className="flex items-center gap-2 text-sm text-muted-foreground min-w-0">
                <BookOpen className="h-3 w-3 shrink-0" />
                <span className="truncate" data-testid={`note-book-${note.id}`}>
                  {note.book?.title || 'Unknown Book'}
                </span>
                {note.page && (
                  <>
                    <span>â€¢</span>
                    <span data-testid={`note-page-${note.id}`}>p.{note.page}</span>
                  </>
                )}
              </div>
            </div>

            <div className="flex items-center gap-1 opacity-0 group-hover:opacity-100 transition-opacity">
              <Button
                variant="ghost"
                size="sm"
                onClick={() => onEditNote(note)}
                data-testid={`button-edit-${note.id}`}
              >
                <Edit className="h-3 w-3" />
              </Button>

              <DropdownMenu>
                <DropdownMenuTrigger asChild>
                  <Button
                    variant="ghost"
                    size="sm"
                    data-testid={`button-menu-${note.id}`}
                  >
                    <MoreHorizontal className="h-3 w-3" />
                  </Button>
                </DropdownMenuTrigger>
                <DropdownMenuContent align="end">
                  <DropdownMenuItem onClick={() => onEditNote(note)}>
                    <Edit className="h-4 w-4 mr-2" />
                    Edit
                  </DropdownMenuItem>
                  <DropdownMenuItem onClick={() => onMakeRecall(note.id)}>
                    <Brain className="h-4 w-4 mr-2" />
                    Make Recall
                  </DropdownMenuItem>
                  <DropdownMenuItem 
                    onClick={() => onDeleteNote(note.id)}
                    className="text-destructive focus:text-destructive"
                  >
                    Delete
                  </DropdownMenuItem>
                </DropdownMenuContent>
              </DropdownMenu>
            </div>
          </div>

          {/* Note content (2-4 lines with truncation) */}
          <div className="mb-3">
            <p 
              className="text-sm leading-relaxed line-clamp-3"
              data-testid={`note-content-${note.id}`}
            >
              {note.content}
            </p>
          </div>

          {/* Footer with timestamp and tags */}
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-2 text-xs text-muted-foreground">
              <Calendar className="h-3 w-3" />
              <span data-testid={`note-timestamp-${note.id}`}>
                {formatDistanceToNow(new Date(note.createdAt), { addSuffix: true })}
              </span>
            </div>

            {note.tags && note.tags.length > 0 && (
              <div className="flex gap-1 flex-wrap" data-testid={`note-tags-${note.id}`}>
                {note.tags.slice(0, 3).map((tag, index) => (
                  <Badge 
                    key={index} 
                    variant="outline" 
                    className="text-xs px-1 py-0"
                    data-testid={`note-tag-${tag}-${note.id}`}
                  >
                    {tag}
                  </Badge>
                ))}
                {note.tags.length > 3 && (
                  <Badge variant="outline" className="text-xs px-1 py-0">
                    +{note.tags.length - 3}
                  </Badge>
                )}
              </div>
            )}
          </div>
        </CardContent>
      </Card>
    );
  };

  if (notes.length === 0) {
    return (
      <div className="flex flex-col items-center justify-center py-12 text-center">
        <Quote className="h-12 w-12 text-muted-foreground mb-4" />
        <h3 className="text-lg font-semibold mb-2">No notes found</h3>
        <p className="text-muted-foreground max-w-md">
          Start capturing your thoughts and insights while reading. Use the quick capture above to add your first note.
        </p>
      </div>
    );
  }

  return (
    <div
      ref={parentRef}
      className="flex-1 overflow-auto"
      style={{ height: '100%' }}
      data-testid="notes-list-container"
    >
      <div
        style={{
          height: virtualizer.getTotalSize(),
          width: '100%',
          position: 'relative',
        }}
      >
        {virtualItems.map((virtualItem) => {
          const note = notes[virtualItem.index];
          return (
            <div
              key={virtualItem.key}
              data-index={virtualItem.index}
              ref={virtualizer.measureElement}
              style={{
                position: 'absolute',
                top: 0,
                left: 0,
                width: '100%',
                transform: `translateY(${virtualItem.start}px)`,
              }}
              className="px-4 pb-4"
            >
              {renderNote(note)}
            </div>
          );
        })}
      </div>
    </div>
  );
}

client/src/components/notes/quick-captur.content:
import { useState, useEffect } from 'react';
import { useMutation, useQueryClient, useQuery } from '@tanstack/react-query';
import { 
  PenTool, 
  Quote, 
  Highlighter, 
  FileText, 
  CheckSquare, 
  Mic, 
  Camera, 
  Clipboard,
  ChevronDown,
  Brain
} from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Badge } from '@/components/ui/badge';
import { Switch } from '@/components/ui/switch';
import { Label } from '@/components/ui/label';
import { Card, CardContent } from '@/components/ui/card';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import { apiRequest } from '@/lib/queryClient';
import type { Book, InsertBookNote } from '@shared/schema';

interface QuickCaptureProps {
  isCollapsed: boolean;
  defaultBook?: Book | null;
  defaultPage?: number;
  onCapture?: (note: any) => void;
}

const NOTE_TYPES = [
  { id: 'note', label: 'Note', icon: PenTool, color: 'bg-blue-100 text-blue-700' },
  { id: 'quote', label: 'Quote', icon: Quote, color: 'bg-green-100 text-green-700' },
  { id: 'highlight', label: 'Highlight', icon: Highlighter, color: 'bg-yellow-100 text-yellow-700' },
  { id: 'summary', label: 'Summary', icon: FileText, color: 'bg-purple-100 text-purple-700' },
  { id: 'action', label: 'Action', icon: CheckSquare, color: 'bg-orange-100 text-orange-700' },
];

export function QuickCaptureInline({ 
  isCollapsed, 
  defaultBook, 
  defaultPage,
  onCapture 
}: QuickCaptureProps) {
  const queryClient = useQueryClient();
  const [content, setContent] = useState('');
  const [selectedBook, setSelectedBook] = useState<string>(defaultBook?.id || '');
  const [page, setPage] = useState<string>(defaultPage?.toString() || '');
  const [noteType, setNoteType] = useState<string>('note');
  const [makeRecall, setMakeRecall] = useState(false);
  const [addTag, setAddTag] = useState(false);
  const [newTag, setNewTag] = useState('');
  const [tags, setTags] = useState<string[]>([]);
  const [bookSelectOpen, setBookSelectOpen] = useState(false);
  const [typeSelectOpen, setTypeSelectOpen] = useState(false);

  // Fetch currently reading books for quick selection
  const { data: currentlyReadingBooks = [] } = useQuery({
    queryKey: ['/api/books/currently-reading'],
    queryFn: async () => {
      const response = await fetch('/api/books/currently-reading');
      if (!response.ok) throw new Error('Failed to fetch books');
      const data = await response.json();
      return data.map((book: any) => ({
        ...book,
        addedAt: new Date(book.addedAt),
        lastReadAt: book.lastReadAt ? new Date(book.lastReadAt) : null,
        startedAt: book.startedAt ? new Date(book.startedAt) : null,
        completedAt: book.completedAt ? new Date(book.completedAt) : null,
      })) as Book[];
    },
  });

  // Auto-fill book and page from Currently Reading if not provided
  useEffect(() => {
    if (!defaultBook && currentlyReadingBooks.length > 0 && !selectedBook) {
      const firstBook = currentlyReadingBooks[0];
      setSelectedBook(firstBook.id);
      if (!defaultPage && firstBook.currentPage) {
        setPage(firstBook.currentPage.toString());
      }
    }
  }, [currentlyReadingBooks, defaultBook, defaultPage, selectedBook]);

  // Create note mutation
  const createNoteMutation = useMutation({
    mutationFn: async (noteData: InsertBookNote) => {
      const response = await fetch('/api/notes', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(noteData),
      });
      if (!response.ok) throw new Error('Failed to create note');
      return await response.json();
    },
    onSuccess: (newNote) => {
      // Invalidate notes queries
      queryClient.invalidateQueries({ queryKey: ['/api/notes'] });

      // Reset form
      setContent('');
      setNewTag('');
      setTags([]);
      setMakeRecall(false);
      setAddTag(false);

      // Keep book and page for convenience
      onCapture?.(newNote);
    },
    onError: (error) => {
      console.error('Failed to create note:', error);
    },
  });

  const handleSubmit = () => {
    if (!content.trim() || !selectedBook) return;

    const noteData: InsertBookNote = {
      bookId: selectedBook,
      content: content.trim(),
      noteType: noteType as any,
      page: page ? parseInt(page) : undefined,
      tags: tags,
    };

    createNoteMutation.mutate(noteData);
  };

  const handleAddTag = () => {
    if (newTag.trim() && !tags.includes(newTag.trim())) {
      setTags([...tags, newTag.trim()]);
      setNewTag('');
    }
  };

  const removeTag = (tagToRemove: string) => {
    setTags(tags.filter(tag => tag !== tagToRemove));
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && e.metaKey) {
      handleSubmit();
    }
  };

  const selectedType = NOTE_TYPES.find(type => type.id === noteType) || NOTE_TYPES[0];

  if (isCollapsed) {
    return null; // Will be handled by bottom sheet instead
  }

  return (
    <Card className="border-dashed" data-testid="card-quick-capture">
      <CardContent className="p-4 space-y-4">
        {/* Note content input */}
        <div className="space-y-2">
          <Label htmlFor="note-content" className="text-sm font-medium">Note content</Label>
          <Textarea
            id="note-content"
            placeholder={`Write your ${selectedType.label.toLowerCase()}...`}
            value={content}
            onChange={(e) => setContent(e.target.value)}
            onKeyDown={handleKeyPress}
            className="min-h-[80px] resize-none"
            data-testid="textarea-note-content"
            aria-describedby="content-help"
          />
          <div id="content-help" className="text-xs text-muted-foreground">
            âŒ˜+Enter to save â€¢ {content.length} characters
          </div>
        </div>

        {/* Book and page selectors */}
        <div className="grid grid-cols-3 gap-3">
          <div className="col-span-2 space-y-2">
            <Label htmlFor="select-note-book-trigger">Book</Label>
            <Select 
              value={selectedBook} 
              onValueChange={setSelectedBook}
              open={bookSelectOpen}
              onOpenChange={setBookSelectOpen}
            >
              <SelectTrigger 
                id="select-note-book-trigger"
                data-testid="select-note-book"
                aria-label="Select book"
              >
                <SelectValue placeholder="Select book" />
              </SelectTrigger>
              <SelectContent 
                data-testid="listbox-note-book"
                className="z-50"
                position="popper"
                sideOffset={4}
                aria-labelledby="select-note-book-trigger"
              >
                {currentlyReadingBooks.map(book => (
                  <SelectItem 
                    key={book.id} 
                    value={book.id}
                    data-testid={`option-book-${book.id}`}
                  >
